<script>
export default {
  // Adds use of global variables
  inject: ["global"],
  props: ['bldgSVG'],
  watch: {
    'bldgSVG': {
      handler() {
        if (this.bldgSVG) { // selected
          this.bringToFront(this.bldgSVG);
          this.$refs.blurRef.setAttribute('stdDeviation', 0.16);
        } else { // unselected
          setTimeout(this.windowEventHandler, 800);
          this.$refs.blurRef.setAttribute('stdDeviation', 0);
        }
      }
    },
    // This is run once, for a first calculation
    'global.firstCalc': {
      handler() {
        if (this.global.firstCalc) {
          this.applyBuildingColors();
        }
      }
    }
  },
  // General local variables
  data() {
    return {
      threshold: 1,
      doResize: "",
      zoom: 0
          }
  },
  updated() {
    this.$nextTick(() => {
      this.applyBuildingColors()
    })
    // if (this.ratio < this.threshold) {
    //   portraitMode = true;
    // } else {
    //   portraitMode = false;
    // }
  },
  mounted() {
    // Turn on the map
    map.style.opacity = 1
    setTimeout(() => map.style.transition = "transform .2s, width .4s", 500)
    // Check for resizing of window
    window.addEventListener("resize", this.windowResizeTimeout)
    // Allow for the scroll wheel to zoom the map
    window.addEventListener("wheel", this.onMouseScroll);
    // Handles changes to the window
    this.windowEventHandler()
    window.addEventListener("mousedown", () => {
      window.addEventListener("mousemove", this.onMouseDrag);
    });
    window.addEventListener("mouseup", () => {
      window.removeEventListener("mousemove", this.onMouseDrag);
    });
  },
  methods: {
    onMouseScroll({deltaX,deltaY}) {
      if (!this.global.sFocus && !this.global.bldg){
        let dirwheel = 0;
        if (deltaY>0) {
          dirwheel = -1;
        } else if (deltaY<0) {
          dirwheel = 1;
        }

        let x = window.innerWidth;
        let y = window.innerHeight;
        let ratio = x / y;
        let portraitMode = false;
        if (ratio < this.threshold) {
          portraitMode = true;
        }
        let tempZoom=0;
        if (portraitMode) {
          tempZoom = y/50+this.zoom+dirwheel*10;
        } else {
          tempZoom = x/50+this.zoom+dirwheel*10;
        }
        
        
        this.zoom +=dirwheel*10;
        if (dirwheel == -1 && this.zoom <= 40) this.zoom  = 37.5 - (40 - this.zoom)*0.75;
        if (dirwheel == 1 && this.zoom >= 60) this.zoom  = 62.5 + (this.zoom - 60)*0.75;
        // console.log(dirwheel, this.zoom)
        this.windowEventHandler();
      }
    },
    onMouseDrag({movementX, movementY}) {
      // if (!this.global.bldg) {
      //   // let mouseerr = 0.0;
      //   // let x = (-mouseerr<movementX&&movementX<mouseerr)?0:movementX;
      //   // let y = (-mouseerr<movementY&&movementY<mouseerr)?0:movementY;
      //   let changeX = (movementX*100*(this.zoom+100)/100);
      //   let changeY = (movementY*100*(this.zoom+100)/100);

      //   mapBox.style.left = mapBox.offsetLeft + changeX + "px"; 
      //   mapBox.style.top = mapBox.offsetTop + changeY + "px";
      // }
    },
    // Applys the color of the building based on availability
    applyBuildingColors() {
      let colors = [
        "#eff5de", // >0%
        "#fef6c0", // >10%
        "#feeea4", // >20%
        "#fdd884", // >30%
        "#fdc675", // >40%
        "#fcad60", // >50%
        "#f99a57", // >60%
        "#f57948", // >70%
        "#eb6045", // >80%
        "#d7434b", // >90%
      ]
      for (const b of buildings.children) {
        let bldgHeat = 0, fill = ''
        try {
          bldgHeat = this.global.data[b.id].meta.heat
        } catch { console.warn(`Can't color buildings w/o classes`) }
        if (bldgHeat >= .9)      fill = colors[9]
        else if (bldgHeat >= .8) fill = colors[8]
        else if (bldgHeat >= .7) fill = colors[7]
        else if (bldgHeat >= .6) fill = colors[6]
        else if (bldgHeat >= .5) fill = colors[5]
        else if (bldgHeat >= .4) fill = colors[4]
        else if (bldgHeat >= .3) fill = colors[3]
        else if (bldgHeat >= .2) fill = colors[2]
        else if (bldgHeat >= .1) fill = colors[1]
        else                    fill = colors[0]

        b.style.fill = fill
        document.documentElement.style.setProperty('--heatColor', tinycolor(fill).darken(25).toString());
        let border = tinycolor(fill).darken(25).toString()
        b.style.stroke = border

      }
    },
    bringToFront(b) {
      let group = b.parentNode
      group.appendChild(b)
    },
    // Used so resizing isnt called forever, 
    // Stops calling when resize hasnt been changed for 300 seconds
    windowResizeTimeout() {
      if (!this.global.bldg) {
        clearTimeout(this.doResize);
        this.doResize = setTimeout(this.windowEventHandler, 300);
      }
    },
    // Handles events when resize is changed
    windowEventHandler() {
      let x = window.innerWidth;
      let y = window.innerHeight;
      let ratio = x / y;
      if (ratio < this.threshold) { // portrait mode
        map.style.transform = `scale(${y/50+this.zoom})` + `rotate(90deg)`
      }
      else // landscape mode
        map.style.transform = `scale(${x/50+this.zoom})`
    }
  }
}
</script>

